import os, platform, time
import datetime

def creation_date(path_to_file):
    """
    Try to get the date that a file was created, falling back to when it was
    last modified if that isn't possible.
    See http://stackoverflow.com/a/39501288/1709587 for explanation.
    """
    if platform.system() == 'Windows':
        print("last modified: %s" % time.ctime(os.path.getmtime(path_to_file)))
        modtime = time.ctime(os.path.getmtime(path_to_file))
        
        print("created: %s" % time.ctime(os.path.getctime(path_to_file)))
        modtime = datetime.datetime.strptime(modtime, "%a %b %d %H:%M:%S %Y")
        modtime = datetime.datetime.strftime(modtime, "%Y-%m-%d")
        return modtime

